        quantity: cartItem.quantity + 1 
    }: cartItem)
}

    return [
        ...cartItems,
        {...productToAdd,
        quantity: 1
    }
    ]
}

export const CartContext = createContext({



    isCartOpen: false,
    setIsCartOpen: () => {},
    cartItems: [],
    addItemToCart: () => {},
    amountInCart: 0,
    setAmountInCart: () => {}

})


