},[cartItems])

 const addItemToCart = (productToAdd) => {
console.log('render')
setCartItems(addCartItem(cartItems, productToAdd))
setAmountInCart(prev => prev + 1)

 }
 const value = {isCartOpen, setIsCartOpen, addItemToCart, cartItems, setAmountInCart, amountInCart}
    return(
        <CartContext.Provider value={value}>{children}

        </CartContext.Provider>
    )
}
